export default {
    namespaced: true,
    state() {
        return {
            productsList: [],
            filteredProductList: []
        }
    },
    mutations: {
        setProductsToState: (state, result) => {
            state.productsList = result;
        },

        setFilteredProductsToState: (state, filteredProductList) => {
            state.filteredProductList = filteredProductList;

        }

        // filter(state, userSearch){
