export default {
    namespaced: true,
    state() {
        return {
            cartList: [],
        }
    },
    mutations: {
        // addItem(state, item) {
        //     state.cartList = [...state.cartList, item];
        // },
        setProductsToCart: (state, result) => {
            state.cartList = result;
            // state.filteredProductList = result;
        },
    },
    actions: {
        getJson({commit}, url){
            return fetch(url)
                .then(result => result.json())
                .then(result => {
                    commit('setProductsToCart', result.contents);
                })
                .catch(error => {
                    console.log(error)
                })
        },
        // putJson(url, data){
