export default {
    namespaced: true,
    state() {
        return {
            cartList: [],
        }
    },
    mutations: {
        addItem(state, item) {
            state.cartList = [...state.cartList, item];
        },
        setProductsToCart: (state, result) => {
            state.cartList = result;
            // state.filteredProductList = result;
        },
    },
    actions: {
        getJson({}, url){
            return fetch(url)
                .then(result => result.json())
                .then(result => {
                    commit('setProductsToCart', result);
                })
                .catch(error => {
                    console.log(error)
                })
        },
        putJson(url, data){
            return fetch(url, {
                method: 'PUT',
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(data)
            })
                .then(result => result.json())
                .catch(error => {
                    // console.log(error)
                    this.$refs.error.text = error;
                })
        },
    },
}
